print("len(LADS)", len(sgNodes))
print("len(R) ", len(R))
print("len(my)", len(LA))


G = nx.Graph()

nc = []
for u in allNodes:
    G.add_node(u)
    if u in LA.intersection(sgNodes):
        nc.append("black")
    elif u in LA.intersection(R):
        nc.append("red")
    elif u in sgNodes:
        nc.append("green")
    elif u in R:
        nc.append("blue")
    elif u in LA:
        nc.append("yellow")
    else:
        print("error")



with open(sys.argv[1], "r") as f:
    for l in f:
        u, v = map(int, l.split())
        if u in allNodes and v in allNodes:
            G.add_edge(u, v)

pos = nx.spring_layout(G)  # 选择布局算法;
nx.draw(G, pos, node_size=10, with_labels=False,
        width=0.03, node_color=nc)
plt.show()